// x를 갈 수 있다는 걸 알고 방문한 상태
static void dfs(int x){                   // -> 모든 정점이 x로 한번씩만 O(V) 
		// x 방문
		visit[x] = true;

		// x에서 갈 수 있는 곳들을 모두 방문
		for(int y : x에서 갈 수 있는 점 들){       // -> 인접행렬 O(V) || 인접리스트 O(deg(x))
				if(visit[y])     // visit[y]가 true면 이 y는 무시
					continue;

				// 아직 안가본 y라면 가보자
				dfs(y);
		}
}

