// 물통의 현재 상태와, 물을 붓는 행위를 관리하는 구조체
class State{
    int[] X;

    State(int[] _X){
        X = new int[3];
        for (int i = 0; i < 3; i++) X[i] = _X[i];
    }

    State move(int from, int to, int[] Limit) {
        int[] nX = new int[]{X[0], X[1], X[2]};

        if (X[from] + X[to] >= Limit[to]) {
            nX[from] -= Limit[to] - nX[to];
            nX[to] = Limit[to];
        }else{
            nX[to] = nX[from] + nX[to] ;
            nX[from] = 0;
        }
        return new State(nX);
    }
};
